//     height: 10px;
//     background-color: #000;
//     cursor: row-resize;
//     position: absolute;
//     bottom: 0;
//     left: 0;
// }


const Code = () => {
    const resizableContainer = document.querySelector('.resizable-container');
const resizer = document.querySelector('.resizer');

resizer.addEventListener('mousedown', function(e) {
    document.addEventListener('mousemove', resize);
    document.addEventListener('mouseup', stopResize);
});

function resize(e) {
    const height = e.clientY - resizableContainer.getBoundingClientRect().top;
    resizableContainer.style.height = `${height}px`;
}

function stopResize() {
    document.removeEventListener('mousemove', resize);
    document.removeEventListener('mouseup', stopResize);
}


    const { html, css, js, setHtml, setCss, setJs } = useContext(DataContext);

    return (
        <div className="resizable-container" style={resizable-container}>
        <div className="resizer" style={resizer}>
        <Container>
            <Editor 
                language="xml"
                heading="HTML"
                value={html}
                onChange={setHtml}
                icon='/'
                color='#FF3C41'
            />
            <Editor 
                language="css"
                heading="CSS"
                value={css}
                onChange={setCss}
                icon='*'
                color='#0EBEFF'
            />
            <Editor 
                language="javascript"
                heading="JS"
                value={js}
                onChange={setJs}
                icon='( )'
                color='#FCD000'
            />
        </Container>
        </div>
    </div>
    )
}

export default Code;
