import React, { useContext, useEffect } from 'react';
import Editor from './Editor';
import { Box, styled } from '@mui/material';
import { DataContext } from '../context/DataProvider';

// Styled components for the container and resizer
const Container = styled(Box)`
  background-color: #060606;
  height: 60vh;
  display: flex;
`;

const resizableContainerStyle = {
  width: '500px',
  height: '600px',
  backgroundColor: '#ffffff',
  border: '2px solid #000000',
  position: 'relative',
  resize: 'vertical',
  overflow: 'auto',
};

const resizerStyle = {
  width: '100%',
  height: '10px',
  backgroundColor: '#000',
  cursor: 'row-resize',
  position: 'absolute',
  bottom: '0',
  left: '0',
};

const Code = () => {
  const { html, css, js, setHtml, setCss, setJs } = useContext(DataContext);

  useEffect(() => {
    const resizableContainer = document.querySelector('.resizable-container');
    const resizer = document.querySelector('.resizer');

    const resize = (e) => {
      const height = e.clientY - resizableContainer.getBoundingClientRect().top;
      resizableContainer.style.height = `${height}px`;
    };

    const stopResize = () => {
      document.removeEventListener('mousemove', resize);
      document.removeEventListener('mouseup', stopResize);
    };

    resizer.addEventListener('mousedown', (e) => {
      document.addEventListener('mousemove', resize);
      document.addEventListener('mouseup', stopResize);
    });

    return () => {
      resizer.removeEventListener('mousedown', resize);
      document.removeEventListener('mousemove', resize);
      document.removeEventListener('mouseup', stopResize);
    };
  }, []);

  return (
    <div className="resizable-container" style={resizableContainerStyle}>
      <div className="resizer" style={resizerStyle}></div>
      <Container>
        <Editor
          language="xml"
          heading="HTML"
          value={html}
          onChange={setHtml}
          icon="/"
          color="#FF3C41"
        />
        <Editor
          language="css"
          heading="CSS"
          value={css}
          onChange={setCss}
          icon="*"
          color="#0EBEFF"
        />
        <Editor
          language="javascript"
          heading="JS"
          value={js}
          onChange={setJs}
          icon="( )"
          color="#FCD000"
        />
      </Container>
    </div>
  );
};

export default Code;
